contracts/CampaignFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./SoulboundToken.sol";
import "./NGORegistry.sol";

/**
 * @title CampaignFactory
 * @notice Factory contract for creating and managing charity campaigns
 * @dev Handles campaign lifecycle, milestones, and fund distribution
 */
contract CampaignFactory is AccessControl, Pausable, ReentrancyGuard {
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");

    enum CampaignStatus {
        Draft,
        Active,
        Paused,
        Completed,
        Cancelled
    }

    enum MilestoneStatus {
        Pending,
        InProgress,
        Completed,
        Failed
    }

    struct Milestone {
        string description;
        uint256 targetAmount;
        uint256 deadline;
        MilestoneStatus status;
        string proofHash; // IPFS hash of proof documents
        uint256 completedAt;
    }

    struct Campaign {
        uint256 campaignId;
        address ngoAddress;
        uint256 ngoId;
        string title;
        string description;
        string category;
        uint256 targetAmount;
        uint256 raisedAmount;
        uint256 startDate;
        uint256 endDate;
        CampaignStatus status;
        uint256 totalDonors;
        uint256 totalBeneficiaries;
        string documentsHash; // IPFS hash of campaign documents
        bool hasMilestones;
        uint256 createdAt;
    }

    // Campaign ID counter
    uint256 private _campaignIdCounter;

    // Reference to NGO Registry
    NGORegistry public ngoRegistry;

    // Reference to Soulbound Token contract
    SoulboundToken public soulboundToken;

    // Mapping from campaign ID to campaign data
    mapping(uint256 => Campaign) public campaigns;

    // Mapping from campaign ID to milestones
    mapping(uint256 => Milestone[]) public campaignMilestones;

    // Mapping from campaign ID to donor addresses
    mapping(uint256 => address[]) public campaignDonors;

    // Mapping from campaign ID to donor amount
    mapping(uint256 => mapping(address => uint256)) public campaignDonations;

    // Mapping from campaign ID to funds held
    mapping(uint256 => uint256) public campaignFunds;

    // Platform fee percentage (in basis points, 100 = 1%)
    uint256 public platformFeePercentage = 250; // 2.5%

    // Platform fee collector address
    address public feeCollector;

    // Events
    event CampaignCreated(
        uint256 indexed campaignId,
        address indexed ngo,
        string title,
        uint256 targetAmount
    );

    event CampaignStatusChanged(
        uint256 indexed campaignId,
        CampaignStatus oldStatus,
        CampaignStatus newStatus
    );

    event DonationReceived(
        uint256 indexed campaignId,
        address indexed donor,
        uint256 amount
    );

    event MilestoneAdded(uint256 indexed campaignId, uint256 milestoneIndex, string description);

    event MilestoneCompleted(uint256 indexed campaignId, uint256 milestoneIndex);

    event FundsReleased(uint256 indexed campaignId, uint256 amount, address recipient);

    event BeneficiaryTokenIssued(
        uint256 indexed campaignId,
        address indexed beneficiary,
        uint256 tokenId
    );

    constructor(address _ngoRegistry, address _soulboundToken, address _feeCollector) {
        require(_ngoRegistry != address(0), "Invalid NGO Registry address");
        require(_soulboundToken != address(0), "Invalid Soulbound Token address");
        require(_feeCollector != address(0), "Invalid fee collector address");

        ngoRegistry = NGORegistry(_ngoRegistry);
        soulboundToken = SoulboundToken(_soulboundToken);
        feeCollector = _feeCollector;

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);
    }

    /**
     * @notice Create a new campaign
     * @param title Campaign title
     * @param description Campaign description
     * @param category Campaign category
     * @param targetAmount Target fundraising amount
     * @param duration Campaign duration in seconds
     * @param documentsHash IPFS hash of campaign documents
     */
    function createCampaign(
        string memory title,
        string memory description,
        string memory category,
        uint256 targetAmount,
        uint256 duration,
        string memory documentsHash
    ) external whenNotPaused returns (uint256) {
        require(ngoRegistry.isNGOVerified(msg.sender), "Only verified NGOs can create campaigns");
        require(targetAmount > 0, "Target amount must be greater than 0");
        require(duration > 0, "Duration must be greater than 0");

        uint256 campaignId = ++_campaignIdCounter;
        uint256 ngoId = ngoRegistry.addressToNgoId(msg.sender);

        campaigns[campaignId] = Campaign({
            campaignId: campaignId,
            ngoAddress: msg.sender,
            ngoId: ngoId,
            title: title,
            description: description,
            category: category,
            targetAmount: targetAmount,
            raisedAmount: 0,
            startDate: block.timestamp,
            endDate: block.timestamp + duration,
            status: CampaignStatus.Active,
            totalDonors: 0,
            totalBeneficiaries: 0,
            documentsHash: documentsHash,
            hasMilestones: false,
            createdAt: block.timestamp
        });

        emit CampaignCreated(campaignId, msg.sender, title, targetAmount);

        return campaignId;
    }

    /**
     * @notice Add milestone to campaign
     * @param campaignId ID of the campaign
     * @param description Milestone description
     * @param targetAmount Target amount for this milestone
     * @param deadline Milestone deadline
     */
    function addMilestone(
        uint256 campaignId,
        string memory description,
        uint256 targetAmount,
        uint256 deadline
    ) external {
        Campaign storage campaign = campaigns[campaignId];
        require(campaign.campaignId != 0, "Campaign does not exist");
        require(campaign.ngoAddress == msg.sender, "Only campaign creator can add milestones");
        require(campaign.status == CampaignStatus.Draft || campaign.status == CampaignStatus.Active, "Cannot modify campaign");

        campaignMilestones[campaignId].push(Milestone({
            description: description,
            targetAmount: targetAmount,
            deadline: deadline,
            status: MilestoneStatus.Pending,
            proofHash: "",
            completedAt: 0
        }));

        campaign.hasMilestones = true;

        emit MilestoneAdded(campaignId, campaignMilestones[campaignId].length - 1, description);
    }

    /**
     * @notice Donate to a campaign
     * @param campaignId ID of the campaign
     */
    function donate(uint256 campaignId) external payable nonReentrant whenNotPaused {
        Campaign storage campaign = campaigns[campaignId];
        require(campaign.campaignId != 0, "Campaign does not exist");
        require(campaign.status == CampaignStatus.Active, "Campaign is not active");
        require(block.timestamp <= campaign.endDate, "Campaign has ended");
        require(msg.value > 0, "Donation amount must be greater than 0");

        // Record donation
        if (campaignDonations[campaignId][msg.sender] == 0) {
            campaignDonors[campaignId].push(msg.sender);
            campaign.totalDonors++;
        }

        campaignDonations[campaignId][msg.sender] += msg.value;
        campaign.raisedAmount += msg.value;
        campaignFunds[campaignId] += msg.value;

        emit DonationReceived(campaignId, msg.sender, msg.value);

        // Auto-complete if target reached
        if (campaign.raisedAmount >= campaign.targetAmount) {
            _updateCampaignStatus(campaignId, CampaignStatus.Completed);
        }
    }

    /**
     * @notice Issue beneficiary token
     * @param campaignId ID of the campaign
     * @param beneficiary Address of the beneficiary
     * @param entitledAmount Amount entitled to beneficiary
     * @param serviceType Type of service
     * @param validityPeriod Token validity period
     */
    function issueBeneficiaryToken(
        uint256 campaignId,
        address beneficiary,
        uint256 entitledAmount,
        string memory serviceType,
        uint256 validityPeriod
    ) external nonReentrant returns (uint256) {
        Campaign storage campaign = campaigns[campaignId];
        require(campaign.campaignId != 0, "Campaign does not exist");
        require(campaign.ngoAddress == msg.sender, "Only campaign creator can issue tokens");
        require(campaign.status == CampaignStatus.Active, "Campaign must be active");
        require(campaignFunds[campaignId] >= entitledAmount, "Insufficient campaign funds");
        require(entitledAmount > 0, "Entitled amount must be greater than 0");
        require(validityPeriod > 0, "Validity period must be greater than 0");

        // Mint soulbound token to beneficiary
        uint256 tokenId = soulboundToken.mintToken(
            beneficiary,
            campaignId,
            entitledAmount,
            serviceType,
            validityPeriod
        );

        campaign.totalBeneficiaries++;

        // Reserve funds for this token
        campaignFunds[campaignId] -= entitledAmount;

        emit BeneficiaryTokenIssued(campaignId, beneficiary, tokenId);

        return tokenId;
    }

    /**
     * @notice Release funds to service provider after token redemption
     * @param campaignId ID of the campaign
     * @param recipient Address to receive funds
     * @param amount Amount to release
     */
    function releaseFunds(
        uint256 campaignId,
        address payable recipient,
        uint256 amount
    ) external onlyRole(ADMIN_ROLE) nonReentrant {
        Campaign storage campaign = campaigns[campaignId];
        require(campaign.campaignId != 0, "Campaign does not exist");
        require(recipient != address(0), "Invalid recipient");
        require(amount > 0, "Amount must be greater than 0");

        // Calculate platform fee
        uint256 fee = (amount * platformFeePercentage) / 10000;
        uint256 netAmount = amount - fee;

        // Transfer funds
        (bool successFee, ) = payable(feeCollector).call{value: fee}("");
        require(successFee, "Fee transfer failed");

        (bool successNet, ) = recipient.call{value: netAmount}("");
        require(successNet, "Fund transfer failed");

        emit FundsReleased(campaignId, netAmount, recipient);
    }

    /**
     * @notice Complete a milestone
     * @param campaignId ID of the campaign
     * @param milestoneIndex Index of the milestone
     * @param proofHash IPFS hash of completion proof
     */
    function completeMilestone(
        uint256 campaignId,
        uint256 milestoneIndex,
        string memory proofHash
    ) external {
        Campaign storage campaign = campaigns[campaignId];
        require(campaign.campaignId != 0, "Campaign does not exist");
        require(campaign.ngoAddress == msg.sender, "Only campaign creator can complete milestones");
        require(milestoneIndex < campaignMilestones[campaignId].length, "Invalid milestone index");

        Milestone storage milestone = campaignMilestones[campaignId][milestoneIndex];
        require(milestone.status == MilestoneStatus.Pending || milestone.status == MilestoneStatus.InProgress, "Milestone cannot be completed");

        milestone.status = MilestoneStatus.Completed;
        milestone.proofHash = proofHash;
        milestone.completedAt = block.timestamp;

        emit MilestoneCompleted(campaignId, milestoneIndex);
    }

    /**
     * @notice Update campaign status
     */
    function _updateCampaignStatus(uint256 campaignId, CampaignStatus newStatus) internal {
        Campaign storage campaign = campaigns[campaignId];
        CampaignStatus oldStatus = campaign.status;
        campaign.status = newStatus;

        emit CampaignStatusChanged(campaignId, oldStatus, newStatus);
    }

    /**
     * @notice Pause a campaign
     * @param campaignId ID of the campaign
     */
    function pauseCampaign(uint256 campaignId) external {
        Campaign storage campaign = campaigns[campaignId];
        require(campaign.campaignId != 0, "Campaign does not exist");
        require(campaign.ngoAddress == msg.sender || hasRole(ADMIN_ROLE, msg.sender), "Not authorized");
        require(campaign.status == CampaignStatus.Active, "Campaign is not active");

        _updateCampaignStatus(campaignId, CampaignStatus.Paused);
    }

    /**
     * @notice Resume a paused campaign
     * @param campaignId ID of the campaign
     */
    function resumeCampaign(uint256 campaignId) external {
        Campaign storage campaign = campaigns[campaignId];
        require(campaign.campaignId != 0, "Campaign does not exist");
        require(campaign.ngoAddress == msg.sender || hasRole(ADMIN_ROLE, msg.sender), "Not authorized");
        require(campaign.status == CampaignStatus.Paused, "Campaign is not paused");

        _updateCampaignStatus(campaignId, CampaignStatus.Active);
    }

    /**
     * @notice Get campaign details
     * @param campaignId ID of the campaign
     */
    function getCampaign(uint256 campaignId) external view returns (Campaign memory) {
        require(campaigns[campaignId].campaignId != 0, "Campaign does not exist");
        return campaigns[campaignId];
    }

    /**
     * @notice Get campaign milestones
     * @param campaignId ID of the campaign
     */
    function getCampaignMilestones(uint256 campaignId) external view returns (Milestone[] memory) {
        require(campaigns[campaignId].campaignId != 0, "Campaign does not exist");
        return campaignMilestones[campaignId];
    }

    /**
     * @notice Get campaign donors
     * @param campaignId ID of the campaign
     */
    function getCampaignDonors(uint256 campaignId) external view returns (address[] memory) {
        require(campaigns[campaignId].campaignId != 0, "Campaign does not exist");
        return campaignDonors[campaignId];
    }

    /**
     * @notice Get donor's donation amount for a campaign
     * @param campaignId ID of the campaign
     * @param donor Address of the donor
     */
    function getDonorAmount(uint256 campaignId, address donor) external view returns (uint256) {
        return campaignDonations[campaignId][donor];
    }

    /**
     * @notice Update platform fee
     * @param newFeePercentage New fee percentage in basis points
     */
    function updatePlatformFee(uint256 newFeePercentage) external onlyRole(ADMIN_ROLE) {
        require(newFeePercentage <= 1000, "Fee cannot exceed 10%");
        platformFeePercentage = newFeePercentage;
    }

    /**
     * @notice Update fee collector address
     * @param newFeeCollector New fee collector address
     */
    function updateFeeCollector(address newFeeCollector) external onlyRole(ADMIN_ROLE) {
        require(newFeeCollector != address(0), "Invalid address");
        feeCollector = newFeeCollector;
    }

    /**
     * @notice Get campaigns created by a specific NGO
     * @param ngoAddress Address of the NGO
     * @return Array of campaign IDs
     */
    function getCampaignsByNGO(address ngoAddress) external view returns (uint256[] memory) {
        // This is a simplified implementation - you might need to track this differently
        uint256[] memory ngoCampaigns = new uint256[](_campaignIdCounter);
        uint256 count = 0;

        for (uint256 i = 1; i <= _campaignIdCounter; i++) {
            if (campaigns[i].ngoAddress == ngoAddress) {
                ngoCampaigns[count] = i;
                count++;
            }
        }

        // Resize array to actual count
        uint256[] memory result = new uint256[](count);
        for (uint256 i = 0; i < count; i++) {
            result[i] = ngoCampaigns[i];
        }

        return result;
    }

    /**
     * @notice Check if NGO can issue tokens for a campaign
     * @param campaignId ID of the campaign
     * @param ngoAddress Address of the NGO
     * @return True if NGO can issue tokens
     */
    function canIssueTokens(uint256 campaignId, address ngoAddress) external view returns (bool) {
        Campaign storage campaign = campaigns[campaignId];
        return (
            campaign.campaignId != 0 &&
            campaign.ngoAddress == ngoAddress &&
            campaign.status == CampaignStatus.Active &&
            campaignFunds[campaignId] > 0
        );
    }

    /**
     * @notice Get available funds for a campaign
     * @param campaignId ID of the campaign
     * @return Available funds amount
     */
    function getCampaignFunds(uint256 campaignId) external view returns (uint256) {
        require(campaigns[campaignId].campaignId != 0, "Campaign does not exist");
        return campaignFunds[campaignId];
    }

    /**
     * @notice Get total number of campaigns
     * @return Total campaign count
     */
    function getTotalCampaigns() external view returns (uint256) {
        return _campaignIdCounter;
    }

    // Admin functions
    function pause() external onlyRole(ADMIN_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(ADMIN_ROLE) {
        _unpause();
    }

    // Receive function to accept ETH
    receive() external payable {}
}

contracts/NGOGovernance.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title NGOGovernance
 * @notice Decentralized NGO verification through community voting
 */
contract NGOGovernance is AccessControl, ReentrancyGuard {
    bytes32 public constant VERIFIER_ROLE = keccak256("VERIFIER_ROLE");
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");

    // Voting parameters
    uint256 public constant VOTING_DURATION = 7 days;
    uint256 public constant MIN_REPUTATION_TO_VOTE = 100;
    uint256 public constant VOTE_WEIGHT_BASE = 100;
    uint256 public constant REPUTATION_CHANGE_PER_VOTE = 10;

    enum ProposalType { Verification, Challenge }
    enum VoteType { Upvote, Downvote }

    struct Proposal {
        uint256 proposalId;
        uint256 ngoId;
        ProposalType proposalType;
        address proposer;
        string description;
        string evidenceHash;
        uint256 startTime;
        uint256 endTime;
        uint256 upvotes;
        uint256 downvotes;
        uint256 totalVotingPower;
        bool executed;
        mapping(address => Vote) votes;
    }

    struct Vote {
        bool hasVoted;
        VoteType voteType;
        uint256 votingPower;
        uint256 reputationStake;
    }

    struct UserReputation {
        uint256 score;
        uint256 totalProposals;
        uint256 successfulVerifications;
        uint256 successfulChallenges;
        uint256 lastActivity;
    }

    struct ProposalView {
        uint256 proposalId;
        uint256 ngoId;
        ProposalType proposalType;
        address proposer;
        string description;
        uint256 startTime;
        uint256 endTime;
        uint256 upvotes;
        uint256 downvotes;
        bool executed;
    }

    // Mappings
    mapping(uint256 => Proposal) public proposals;
    mapping(address => UserReputation) public userReputations;
    mapping(uint256 => uint256) public ngoVerificationProposals;
    mapping(uint256 => uint256[]) public ngoChallenges;
    mapping(address => bool) public hasVotedOnProposal;

    // Arrays for iteration
    uint256[] public allProposalIds;

    // Counters
    uint256 private _proposalIdCounter;

    // Events
    event ProposalCreated(
        uint256 indexed proposalId,
        uint256 indexed ngoId,
        ProposalType proposalType,
        address indexed proposer,
        string description
    );

    event VoteCast(
        uint256 indexed proposalId,
        address indexed voter,
        VoteType voteType,
        uint256 votingPower
    );

    event ProposalExecuted(
        uint256 indexed proposalId,
        bool verified,
        string reason
    );

    event ReputationUpdated(
        address indexed user,
        uint256 oldScore,
        uint256 newScore
    );

    event NGOChallenged(
        uint256 indexed ngoId,
        uint256 indexed proposalId,
        address indexed challenger
    );

    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    /**
     * @notice Create a verification proposal for an NGO
     */
    function createVerificationProposal(
        uint256 ngoId,
        string memory description,
        string memory evidenceHash
    ) external nonReentrant returns (uint256) {
        require(ngoVerificationProposals[ngoId] == 0, "Proposal already exists");
        require(userReputations[msg.sender].score >= MIN_REPUTATION_TO_VOTE, "Insufficient reputation");

        uint256 proposalId = ++_proposalIdCounter;
        Proposal storage proposal = proposals[proposalId];

        proposal.proposalId = proposalId;
        proposal.ngoId = ngoId;
        proposal.proposalType = ProposalType.Verification;
        proposal.proposer = msg.sender;
        proposal.description = description;
        proposal.evidenceHash = evidenceHash;
        proposal.startTime = block.timestamp;
        proposal.endTime = block.timestamp + VOTING_DURATION;

        ngoVerificationProposals[ngoId] = proposalId;

        // Proposer automatically upvotes
        _castVote(proposalId, VoteType.Upvote, msg.sender);

        emit ProposalCreated(proposalId, ngoId, ProposalType.Verification, msg.sender, description);
        return proposalId;
    }

    /**
     * @notice Challenge a verified NGO
     */
    function challengeNGO(
        uint256 ngoId,
        string memory description,
        string memory evidenceHash
    ) external nonReentrant returns (uint256) {
        require(userReputations[msg.sender].score >= MIN_REPUTATION_TO_VOTE, "Insufficient reputation");

        uint256 proposalId = ++_proposalIdCounter;
        Proposal storage proposal = proposals[proposalId];

        proposal.proposalId = proposalId;
        proposal.ngoId = ngoId;
        proposal.proposalType = ProposalType.Challenge;
        proposal.proposer = msg.sender;
        proposal.description = description;
        proposal.evidenceHash = evidenceHash;
        proposal.startTime = block.timestamp;
        proposal.endTime = block.timestamp + VOTING_DURATION;

        ngoChallenges[ngoId].push(proposalId);

        // Proposer automatically downvotes
        _castVote(proposalId, VoteType.Downvote, msg.sender);

        emit ProposalCreated(proposalId, ngoId, ProposalType.Challenge, msg.sender, description);
        emit NGOChallenged(ngoId, proposalId, msg.sender);

        return proposalId;
    }

    /**
     * @notice Cast vote on a proposal
     */
    function castVote(uint256 proposalId, VoteType voteType) external nonReentrant {
        require(userReputations[msg.sender].score >= MIN_REPUTATION_TO_VOTE, "Insufficient reputation");
        _castVote(proposalId, voteType, msg.sender);
    }

    function _castVote(uint256 proposalId, VoteType voteType, address voter) internal {
        Proposal storage proposal = proposals[proposalId];
        require(block.timestamp <= proposal.endTime, "Voting ended");
        require(!proposal.votes[voter].hasVoted, "Already voted");

        uint256 votingPower = _calculateVotingPower(voter);

        proposal.votes[voter] = Vote({
            hasVoted: true,
            voteType: voteType,
            votingPower: votingPower,
            reputationStake: REPUTATION_CHANGE_PER_VOTE
        });

        if (voteType == VoteType.Upvote) {
            proposal.upvotes += votingPower;
        } else {
            proposal.downvotes += votingPower;
        }

        proposal.totalVotingPower += votingPower;

        emit VoteCast(proposalId, voter, voteType, votingPower);
    }

    /**
     * @notice Execute proposal after voting ends
     */
    function executeProposal(uint256 proposalId) external nonReentrant {
        Proposal storage proposal = proposals[proposalId];
        require(block.timestamp > proposal.endTime, "Voting not ended");
        require(!proposal.executed, "Already executed");

        proposal.executed = true;

        bool passed = proposal.upvotes > proposal.downvotes;
        string memory reason = passed ? "Proposal passed" : "Proposal rejected";

        if (passed) {
            if (proposal.proposalType == ProposalType.Verification) {
                _updateReputationForSuccess(proposalId, true);
            } else {
                _updateReputationForSuccess(proposalId, false);
            }
        } else {
            _updateReputationForFailure(proposalId);
        }

        emit ProposalExecuted(proposalId, passed, reason);
    }

    function _calculateVotingPower(address voter) internal view returns (uint256) {
        UserReputation memory rep = userReputations[voter];
        return (rep.score * VOTE_WEIGHT_BASE) / 1000; // Scale reputation to voting power
    }

    function _updateReputationForSuccess(uint256 proposalId, bool isVerification) internal {
        Proposal storage proposal = proposals[proposalId];

        // Update proposer reputation
        userReputations[proposal.proposer].score += REPUTATION_CHANGE_PER_VOTE * 2;
        userReputations[proposal.proposer].successfulVerifications += isVerification ? 1 : 0;
        userReputations[proposal.proposer].successfulChallenges += isVerification ? 0 : 1;

        // Update voters reputation
        for (uint256 i = 0; i < proposal.totalVotingPower; i++) {
            // In real implementation, you'd iterate through voters
            // This is simplified - you'd need to track voters separately
        }
    }

    function _updateReputationForFailure(uint256 proposalId) internal {
        Proposal storage proposal = proposals[proposalId];

        // Penalize proposer for failed proposal
        if (userReputations[proposal.proposer].score > REPUTATION_CHANGE_PER_VOTE) {
            userReputations[proposal.proposer].score -= REPUTATION_CHANGE_PER_VOTE;
        }
    }

    /**
     * @notice Initialize reputation for new user
     */
    function initializeReputation(address user) external onlyRole(ADMIN_ROLE) {
        require(userReputations[user].score == 0, "Reputation already initialized");
        userReputations[user] = UserReputation({
            score: 500, // Start with base reputation
            totalProposals: 0,
            successfulVerifications: 0,
            successfulChallenges: 0,
            lastActivity: block.timestamp
        });
    }

    /**
     * @notice Update NGO Registry address (called by NGORegistry during deployment)
     */
    function updateNgoRegistry(address _ngoRegistry) external onlyRole(ADMIN_ROLE) {
        require(_ngoRegistry != address(0), "Invalid NGO registry address");
        // This function allows NGORegistry to register itself with governance
        // In a full implementation, you might want to store this reference
    }

    // View functions
    /**
     * @notice Get proposal details in a view-friendly format
     */
    function getProposal(uint256 proposalId) external view returns (
        uint256 ngoId,
        ProposalType proposalType,
        address proposer,
        string memory description,
        uint256 startTime,
        uint256 endTime,
        uint256 upvotes,
        uint256 downvotes,
        bool executed
    ) {
        Proposal storage p = proposals[proposalId];
        return (
            p.ngoId,
            p.proposalType,
            p.proposer,
            p.description,
            p.startTime,
            p.endTime,
            p.upvotes,
            p.downvotes,
            p.executed
        );
    }

    /**
     * @notice Get all proposal IDs for iteration
     */
    function getAllProposalIds() external view returns (uint256[] memory) {
        return allProposalIds;
    }

    /**
     * @notice Get user reputation details
     */
    function getUserReputation(address user) external view returns (
        uint256 score,
        uint256 totalProposals,
        uint256 successfulVerifications,
        uint256 successfulChallenges,
        uint256 lastActivity
    ) {
        UserReputation memory rep = userReputations[user];
        return (
            rep.score,
            rep.totalProposals,
            rep.successfulVerifications,
            rep.successfulChallenges,
            rep.lastActivity
        );
    }

    /**
     * @notice Check if user can vote on proposal
     */
    function canVote(address user, uint256 proposalId) external view returns (bool) {
        if (userReputations[user].score < MIN_REPUTATION_TO_VOTE) return false;
        if (proposals[proposalId].executed) return false;
        if (block.timestamp > proposals[proposalId].endTime) return false;
        if (proposals[proposalId].votes[user].hasVoted) return false;
        return true;
    }
}

contracts/NGORegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "./NGOGovernance.sol";

/**
 * @title NGORegistry
 * @notice Registry for NGO verification and management with decentralized governance
 * @dev Manages NGO registration, KYC verification, and reputation with community voting
 */
contract NGORegistry is AccessControl, Pausable {
    bytes32 public constant VERIFIER_ROLE = keccak256("VERIFIER_ROLE");
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");

    enum NGOStatus {
        Pending,
        Verified,
        Rejected,
        Suspended,
        Blacklisted
    }

    struct NGO {
        uint256 ngoId;
        address ngoAddress;
        string name;
        string registrationNumber;
        string country;
        string category;
        string kycDocumentHash;
        uint256 registeredAt;
        uint256 verifiedAt;
        NGOStatus status;
        uint256 reputationScore;
        uint256 totalCampaigns;
        uint256 totalFundsRaised;
        uint256 totalBeneficiaries;
        bool isActive;
    }

    // NGO ID counter
    uint256 private _ngoIdCounter;

    // Mapping from NGO address to NGO ID
    mapping(address => uint256) public addressToNgoId;

    // Mapping from NGO ID to NGO data
    mapping(uint256 => NGO) public ngos;

    // Mapping from NGO ID to activity logs
    mapping(uint256 => string[]) public ngoActivityLogs;

    // Count of verified NGOs (incremented when NGO moves to Verified)
    uint256 public verifiedCount;

    // Admin verification frozen flag - when true, only governance-based verification is allowed
    bool public adminVerificationFrozen;

    // Governance contract instance
    NGOGovernance public governance;

    // Events
    event NGORegistered(uint256 indexed ngoId, address indexed ngoAddress, string name);
    event NGOVerified(uint256 indexed ngoId, address indexed verifier);
    event NGOStatusChanged(uint256 indexed ngoId, NGOStatus oldStatus, NGOStatus newStatus);
    event NGOReputationUpdated(uint256 indexed ngoId, uint256 oldScore, uint256 newScore);
    event NGOActivityLogged(uint256 indexed ngoId, string activity);
    event NGOGovernanceUpdated(address indexed oldGovernance, address indexed newGovernance);
    event AdminVerificationFrozen(address indexed triggeredBy, uint256 timestamp);

    constructor(address governanceAddress) {
        require(governanceAddress != address(0), "Invalid governance address");

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);
        _grantRole(VERIFIER_ROLE, msg.sender);

        governance = NGOGovernance(governanceAddress);

        emit NGOGovernanceUpdated(address(0), governanceAddress);
    }

    /**
     * @notice Register a new NGO
     */
    function registerNGO(
        address ngoAddress,
        string memory name,
        string memory registrationNumber,
        string memory country,
        string memory category,
        string memory kycDocumentHash
    ) external whenNotPaused returns (uint256) {
        require(ngoAddress != address(0), "Invalid NGO address");
        require(addressToNgoId[ngoAddress] == 0, "NGO already registered");
        require(bytes(name).length > 0, "Name cannot be empty");
        require(bytes(registrationNumber).length > 0, "Registration number required");

        uint256 ngoId = ++_ngoIdCounter;

        ngos[ngoId] = NGO({
            ngoId: ngoId,
            ngoAddress: ngoAddress,
            name: name,
            registrationNumber: registrationNumber,
            country: country,
            category: category,
            kycDocumentHash: kycDocumentHash,
            registeredAt: block.timestamp,
            verifiedAt: 0,
            status: NGOStatus.Pending,
            reputationScore: 500,
            totalCampaigns: 0,
            totalFundsRaised: 0,
            totalBeneficiaries: 0,
            isActive: true
        });

        addressToNgoId[ngoAddress] = ngoId;

        emit NGORegistered(ngoId, ngoAddress, name);
        logActivity(ngoId, "NGO registered");

        return ngoId;
    }

    /**
     * @notice Verify an NGO (called by verifier after KYC check)
     */
    function verifyNGO(uint256 ngoId) external onlyRole(VERIFIER_ROLE) {
        NGO storage ngo = ngos[ngoId];
        require(ngo.ngoId != 0, "NGO does not exist");
        require(ngo.status == NGOStatus.Pending, "NGO is not pending verification");

        require(!adminVerificationFrozen, "Admin verification is frozen");

        NGOStatus oldStatus = ngo.status;
        ngo.status = NGOStatus.Verified;
        ngo.verifiedAt = block.timestamp;

    verifiedCount += 1;

        emit NGOVerified(ngoId, msg.sender);
        emit NGOStatusChanged(ngoId, oldStatus, NGOStatus.Verified);
        logActivity(ngoId, "NGO verified by verifier");
    }

    /**
     * @notice Verify NGO through governance proposal
     */
    function verifyNGOThroughGovernance(uint256 ngoId) external {
        NGO storage ngo = ngos[ngoId];
        require(ngo.ngoId != 0, "NGO does not exist");
        require(ngo.status == NGOStatus.Pending, "NGO is not pending verification");

        // Check if caller has sufficient reputation or is verifier
        (uint256 reputationScore, , , , ) = governance.userReputations(msg.sender);
        // If admin verification is frozen, require reputation-based verification (governance-only)
        if (adminVerificationFrozen) {
            require(reputationScore >= 100, "Governance-only: insufficient reputation");
        } else {
            require(
                hasRole(VERIFIER_ROLE, msg.sender) || reputationScore >= 100,
                "Not authorized"
            );
        }

        NGOStatus oldStatus = ngo.status;
        ngo.status = NGOStatus.Verified;
        ngo.verifiedAt = block.timestamp;

    verifiedCount += 1;

        emit NGOVerified(ngoId, msg.sender);
        emit NGOStatusChanged(ngoId, oldStatus, NGOStatus.Verified);
        logActivity(ngoId, "NGO verified through governance");
    }

    /**
     * @notice Reject an NGO application
     */
    function rejectNGO(uint256 ngoId, string memory reason) external onlyRole(VERIFIER_ROLE) {
        NGO storage ngo = ngos[ngoId];
        require(ngo.ngoId != 0, "NGO does not exist");
        require(ngo.status == NGOStatus.Pending, "NGO is not pending verification");

        NGOStatus oldStatus = ngo.status;
        ngo.status = NGOStatus.Rejected;

        emit NGOStatusChanged(ngoId, oldStatus, NGOStatus.Rejected);
        logActivity(ngoId, string(abi.encodePacked("NGO rejected: ", reason)));
    }

    /**
     * @notice Suspend an NGO
     */
    function suspendNGO(uint256 ngoId, string memory reason) external onlyRole(ADMIN_ROLE) {
        NGO storage ngo = ngos[ngoId];
        require(ngo.ngoId != 0, "NGO does not exist");
        require(ngo.status == NGOStatus.Verified, "Only verified NGOs can be suspended");

        NGOStatus oldStatus = ngo.status;
        ngo.status = NGOStatus.Suspended;
        ngo.isActive = false;

        emit NGOStatusChanged(ngoId, oldStatus, NGOStatus.Suspended);
        logActivity(ngoId, string(abi.encodePacked("NGO suspended: ", reason)));
    }

    /**
     * @notice Suspend NGO through governance challenge
     */
    function suspendNGOThroughGovernance(uint256 ngoId, string memory reason) external {
        NGO storage ngo = ngos[ngoId];
        require(ngo.ngoId != 0, "NGO does not exist");
        require(ngo.status == NGOStatus.Verified, "Only verified NGOs can be suspended");

        // Check if caller has sufficient reputation
        (, uint256 reputationScore, , , ) = governance.userReputations(msg.sender);
        require(reputationScore >= 200, "Insufficient reputation");

        NGOStatus oldStatus = ngo.status;
        ngo.status = NGOStatus.Suspended;
        ngo.isActive = false;

        emit NGOStatusChanged(ngoId, oldStatus, NGOStatus.Suspended);
        logActivity(ngoId, string(abi.encodePacked("NGO suspended through governance: ", reason)));
    }

    /**
     * @notice Blacklist an NGO
     */
    function blacklistNGO(uint256 ngoId, string memory reason) external onlyRole(ADMIN_ROLE) {
        NGO storage ngo = ngos[ngoId];
        require(ngo.ngoId != 0, "NGO does not exist");

        NGOStatus oldStatus = ngo.status;
        ngo.status = NGOStatus.Blacklisted;
        ngo.isActive = false;

        emit NGOStatusChanged(ngoId, oldStatus, NGOStatus.Blacklisted);
        logActivity(ngoId, string(abi.encodePacked("NGO blacklisted: ", reason)));
    }

    /**
     * @notice Update NGO reputation score
     */
    function updateReputation(uint256 ngoId, uint256 newScore) external onlyRole(ADMIN_ROLE) {
        require(newScore <= 1000, "Score must be between 0 and 1000");
        NGO storage ngo = ngos[ngoId];
        require(ngo.ngoId != 0, "NGO does not exist");

        uint256 oldScore = ngo.reputationScore;
        ngo.reputationScore = newScore;

        emit NGOReputationUpdated(ngoId, oldScore, newScore);
    }

    /**
     * @notice Update NGO statistics
     */
    function updateStatistics(
        uint256 ngoId,
        uint256 campaignCount,
        uint256 fundsRaised,
        uint256 beneficiaries
    ) external onlyRole(ADMIN_ROLE) {
        NGO storage ngo = ngos[ngoId];
        require(ngo.ngoId != 0, "NGO does not exist");

        ngo.totalCampaigns = campaignCount;
        ngo.totalFundsRaised = fundsRaised;
        ngo.totalBeneficiaries = beneficiaries;
    }

    /**
     * @notice Log NGO activity
     */
    function logActivity(uint256 ngoId, string memory activity) public {
        require(
            hasRole(ADMIN_ROLE, msg.sender) || 
            hasRole(VERIFIER_ROLE, msg.sender) ||
            ngos[ngoId].ngoAddress == msg.sender,
            "Not authorized"
        );
        require(ngos[ngoId].ngoId != 0, "NGO does not exist");
        ngoActivityLogs[ngoId].push(activity);
        emit NGOActivityLogged(ngoId, activity);
    }

    /**
     * @notice Update governance contract address
     */
    function updateGovernance(address newGovernanceAddress) external onlyRole(ADMIN_ROLE) {
        require(newGovernanceAddress != address(0), "Invalid governance address");
        address oldGovernance = address(governance);
        governance = NGOGovernance(newGovernanceAddress);
        emit NGOGovernanceUpdated(oldGovernance, newGovernanceAddress);
    }

    /**
     * @notice Check if NGO is verified and active
     */
    function isNGOVerified(address ngoAddress) external view returns (bool) {
        uint256 ngoId = addressToNgoId[ngoAddress];
        if (ngoId == 0) return false;
        NGO memory ngo = ngos[ngoId];
        return ngo.status == NGOStatus.Verified && ngo.isActive;
    }

    /**
     * @notice Get NGO details by address
     */
    function getNGOByAddress(address ngoAddress) external view returns (NGO memory) {
        uint256 ngoId = addressToNgoId[ngoAddress];
        require(ngoId != 0, "NGO not found");
        return ngos[ngoId];
    }

    /**
     * @notice Get NGO details by ID
     */
    function getNGOById(uint256 ngoId) external view returns (NGO memory) {
        require(ngos[ngoId].ngoId != 0, "NGO does not exist");
        return ngos[ngoId];
    }

    /**
     * @notice Get NGO activity logs
     */
    function getActivityLogs(uint256 ngoId) external view returns (string[] memory) {
        require(ngos[ngoId].ngoId != 0, "NGO does not exist");
        return ngoActivityLogs[ngoId];
    }

    /**
     * @notice Get total NGO count
     */
    function getTotalNGOs() external view returns (uint256) {
        return _ngoIdCounter;
    }

    // Admin functions
    function pause() external onlyRole(ADMIN_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(ADMIN_ROLE) {
        _unpause();
    }

    /**
     * @notice Freeze admin verification
     * @dev When frozen, verifyNGO (admin/verifier) will be disabled and only governance-based verification will work
     */
    function freezeAdminVerification() external onlyRole(ADMIN_ROLE) {
        require(!adminVerificationFrozen, "Admin verification already frozen");
        adminVerificationFrozen = true;
        emit AdminVerificationFrozen(msg.sender, block.timestamp);
    }
}

contracts/ServiceProviderRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title ServiceProviderRegistry
 * @notice Registry for service providers (hospitals, schools, etc.)
 * @dev Manages provider registration and token redemption authorization
 */
contract ServiceProviderRegistry is AccessControl, Pausable {
    bytes32 public constant VERIFIER_ROLE = keccak256("VERIFIER_ROLE");
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");

    enum ProviderStatus {
        Pending,
        Verified,
        Rejected,
        Suspended
    }

    struct ServiceProvider {
        uint256 providerId;
        address providerAddress;
        string name;
        string registrationNumber;
        string serviceType; // e.g., "Healthcare", "Education"
        string location;
        string verificationDocHash; // IPFS hash
        uint256 registeredAt;
        uint256 verifiedAt;
        ProviderStatus status;
        uint256 totalRedemptions;
        uint256 totalAmountRedeemed;
        bool isActive;
    }

    // Provider ID counter
    uint256 private _providerIdCounter;

    // Mappings
    mapping(address => uint256) public addressToProviderId;
    mapping(uint256 => ServiceProvider) public providers;

    // Events
    event ProviderRegistered(uint256 indexed providerId, address indexed providerAddress, string name);
    event ProviderVerified(uint256 indexed providerId);
    event ProviderStatusChanged(uint256 indexed providerId, ProviderStatus oldStatus, ProviderStatus newStatus);
    event RedemptionRecorded(uint256 indexed providerId, uint256 amount);

    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);
        _grantRole(VERIFIER_ROLE, msg.sender);
    }

    /**
     * @notice Register a new service provider
     */
    function registerProvider(
        address providerAddress,
        string memory name,
        string memory registrationNumber,
        string memory serviceType,
        string memory location,
        string memory verificationDocHash
    ) external whenNotPaused returns (uint256) {
        require(providerAddress != address(0), "Invalid address");
        require(addressToProviderId[providerAddress] == 0, "Provider already registered");

        uint256 providerId = ++_providerIdCounter;

        providers[providerId] = ServiceProvider({
            providerId: providerId,
            providerAddress: providerAddress,
            name: name,
            registrationNumber: registrationNumber,
            serviceType: serviceType,
            location: location,
            verificationDocHash: verificationDocHash,
            registeredAt: block.timestamp,
            verifiedAt: 0,
            status: ProviderStatus.Pending,
            totalRedemptions: 0,
            totalAmountRedeemed: 0,
            isActive: true
        });

        addressToProviderId[providerAddress] = providerId;

        emit ProviderRegistered(providerId, providerAddress, name);

        return providerId;
    }

    /**
     * @notice Verify a service provider
     */
    function verifyProvider(uint256 providerId) external onlyRole(VERIFIER_ROLE) {
        ServiceProvider storage provider = providers[providerId];
        require(provider.providerId != 0, "Provider does not exist");
        require(provider.status == ProviderStatus.Pending, "Provider is not pending");

        ProviderStatus oldStatus = provider.status;
        provider.status = ProviderStatus.Verified;
        provider.verifiedAt = block.timestamp;

        emit ProviderVerified(providerId);
        emit ProviderStatusChanged(providerId, oldStatus, ProviderStatus.Verified);
    }

    /**
     * @notice Record a token redemption
     */
    function recordRedemption(uint256 providerId, uint256 amount) external onlyRole(ADMIN_ROLE) {
        ServiceProvider storage provider = providers[providerId];
        require(provider.providerId != 0, "Provider does not exist");

        provider.totalRedemptions++;
        provider.totalAmountRedeemed += amount;

        emit RedemptionRecorded(providerId, amount);
    }

    /**
     * @notice Check if provider is verified
     */
    function isProviderVerified(address providerAddress) external view returns (bool) {
        uint256 providerId = addressToProviderId[providerAddress];
        if (providerId == 0) return false;
        ServiceProvider memory provider = providers[providerId];
        return provider.status == ProviderStatus.Verified && provider.isActive;
    }

    /**
     * @notice Get provider by address
     */
    function getProviderByAddress(address providerAddress) external view returns (ServiceProvider memory) {
        uint256 providerId = addressToProviderId[providerAddress];
        require(providerId != 0, "Provider not found");
        return providers[providerId];
    }

    // Admin functions
    function pause() external onlyRole(ADMIN_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(ADMIN_ROLE) {
        _unpause();
    }
}

